    private void ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonActionPerformed
        double gross, result, s1 = 0;
        int holiday, overT;
        String s2;
        
        gross = Double.parseDouble(txtGrosspay.getText());
        holiday = Integer.parseInt(txtHoliday.getText());
        overT = Integer.parseInt(txtOvertime.getText());
        
        if (holiday > 3) {
            s1 = (holiday - 3) * 10;
            
        } else {
            s1 = 0;
        }
        
        result = (gross - s1) + (overT * 4);
        
        txtNetpay.setText(String.valueOf(result));
        
    }//GEN-LAST:event_ButtonActionPerformed

