    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
         PrintWriter output = null;
        File out;
        
        try {
            out = new File("Receipt.txt");
            output = new PrintWriter(out);
        } catch (FileNotFoundException ex) {
            Logger.getLogger(CalculateSalary.class.getName()).log(Level.SEVERE, null, ex);
        }
        
        
        output.println("--------------------------------------------------------------------");
        output.println("---------------------------Salary Receipt---------------------------");
        output.println("--------------------------------------------------------------------");
        
        
        output.println();output.println();
        
        output.println("----------------------------Informations----------------------------");
        output.println("Employee ID         : \t\t " + txtID.getText());
        output.println("Gross Payment/Month : \t\t RM" + txtGrosspay.getText());
        output.println("Overtime (Hours)    : \t\t " + txtOvertime.getText() + " hours");
        output.println("Holidays            : \t\t " + txtHoliday.getText() + " days");
        output.println("--------------------------------------------------------------------");
        
        output.println();output.println();
        
        output.println("----------------------------Calculations----------------------------");
        output.println("Gross Payment/Month : \t\t RM" + txtGrosspay.getText());
        output.println("Overtime Payment    : \t\t " + txtOvertime.getText() + " X RM2/hour");
        output.println("Holidays            : \t\t (" + txtHoliday.getText() + " days - 3 days) X RM20/day");
        output.println("--------------------------------------------------------------------");
        output.println("Net Pay = (Gross Payment + Overtime Payment) - Holidays");
        output.println("Net Pay for the month : RM" + txtNetpay.getText());
        output.println("--------------------------------------------------------------------");
        
        output.println();output.println();
        
                
        output.close();
        
        Display d = new Display();
        d.setVisible(true);
        hide();
    }//GEN-LAST:event_jButton2ActionPerformed

